<?php if (!defined('BASEPATH')) exit('No direct script access allowed');class CustomerModel extends CI_Model{    public function detail_data($id)    {        return $this->db->select('*')->from('customers')->where('id', $id)->order_by('id', 'desc')->get()->row();    }    public function create_data($data)    {        $this->db->insert('customers', $data);    }    public function update_data($id, $data)    {        $this->db->where('id', $id)->update('customers', $data);    }    public function delete_data($id, $data)    {        $arr = array('id' => $id, 'status' => '1');        $this->db->where($arr)->update('customers', $data);    }    public function all_data()    {        return $this->db->select('*')->from("customers")->where('status', '1')->order_by('id', 'desc')->get()->result();    }}